function doSearch(){$(".planSearch").empty();$("#planSearch").css("background-color","");var a=$("#Bedrooms").val();var d=$("#Bathrooms").val();var c=$("#Garages").val();var b=[];$("input[type=checkbox]:checked").each(function(){b.push(this.id)});obj=JSON.stringify(b);$.ajax({url:"searchFirst.php",data:{Bedrooms:a,Bathrooms:d,Garages:c,Zones:obj},success:function(e){$("#searchResults").html(e)}})}function showPlan(a){$.ajax({url:"showPlans.php",data:{planId:a},success:function(b){$("#planSearch").css("background-color","#FFF");$("#planSearch").html(b);$("#planCode").text($("#plan"+a).val());$("#planCodeId").val($("#plan"+a).val())}})}function swapImages(a){$(".showBig").attr("src",a)}var added=[];function addItem(b,d){var c=added.indexOf(d);if(c==-1){added.push(d);var a=document.createElement("tr");a.className="form-group form-inline";a.innerHTML='<td><label for="'+d+'" class="search">'+b+'</label></td>                        <td><input type="checkbox" class="form-control" id="'+d+'" name="'+d+'" onchange="doSearch();"></td>';document.getElementById("tblSelection").appendChild(a);doSearch()}};